examples:
/* Simple selectors */
p { color: blue; } /* All paragraphs */
#myId { color: red; } /* Element with id="myId" */
.myClass { color: green; } /* Elements with class="myClass" */
* { margin: 0; } /* All elements */
h1, h2, h3 { font-family: Arial; } /* Multiple elements */

/* Combinator selectors */
div p { color: blue; } /* Paragraphs inside divs */
div > p { color: red; } /* Direct child paragraphs of divs */
h1 + p { margin-top: 0; } /* Paragraph immediately after h1 */
h1 ~ p { color: gray; } /* All paragraphs after h1 */

/* Pseudo-class selectors */
a:hover { color: red; }
input:focus { border: 2px solid blue; }
li:first-child { font-weight: bold; }
tr:nth-child(even) { background-color: #f2f2f2; }

/* Pseudo-element selectors */
p::first-line { font-weight: bold; }
p::first-letter { font-size: 200%; }
.quote::before { content: """; }
.quote::after { content: """; }

/* Attribute selectors */
input[type="text"] { border: 1px solid gray; }
a[href^="https"] { color: green; }
img[alt~="photo"] { border: 2px solid blue; }

topics.syntax:
/* Element selector */
element { }

/* ID selector */
#id { }

/* Class selector */
.class { }

/* Attribute selector */
[attribute="value"] { }

/* External CSS */
<link rel="stylesheet" href="styles.css">

/* Internal CSS */
<style>
  selector { property: value; }
</style>

/* Inline CSS */
<element style="property: value;">

