/* =================================
   MAIN STYLESHEET
   Author: John Doe
   Date: 2024-01-15
   ================================= */

/* Reset styles */
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

/* =================================
   TYPOGRAPHY
   ================================= */

body {
  font-family: Arial, sans-serif; /* Fallback font */
  line-height: 1.6;
  color: #333;
}

/* Heading styles */
h1, h2, h3 {
  margin-bottom: 1rem;
  font-weight: bold;
}

/* =================================
   LAYOUT
   ================================= */

.container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
}

/* Grid system */
.row {
  display: flex;
  flex-wrap: wrap;
  margin: 0 -15px; /* Negative margin for gutters */
}

.col {
  flex: 1;
  padding: 0 15px;
}

/* =================================
   COMPONENTS
   ================================= */

/* Button component */
.btn {
  display: inline-block;
  padding: 10px 20px;
  background-color: #007bff;
  color: white;
  text-decoration: none;
  border-radius: 4px;
  transition: background-color 0.3s ease; /* Smooth hover effect */
}

.btn:hover {
  background-color: #0056b3; /* Darker blue on hover */
}

