  z-index: 1000;
  overflow: hidden;
}

.icon-dropdown:hover .dropdown-content {
  opacity: 1;
  visibility: visible;
  transform: translateY(0);
}

.icon-dropdown .dropdown-item {
  display: flex;
  align-items: center;
  padding: 12px 16px;
  text-decoration: none;
  color: #333;
  transition: background-color 0.3s ease;
}

.icon-dropdown .dropdown-item:hover {
