}

.icon-dropdown .dropdown-description {
  font-size: 12px;
  color: #999;
  margin-top: 2px;
}

/* Animated Dropdown */
.animated-dropdown {
  position: relative;
  display: inline-block;
}

.animated-dropdown .dropdown-content {
  position: absolute;
  top: 100%;
  left: 0;
  background-color: white;
  min-width: 200px;
  box-shadow: 0 8px 16px rgba(0,0,0,0.1);
  border-radius: 8px;
  opacity: 0;
  visibility: hidden;
  transform: scale(0.95) translateY(-10px);
  transition: all 0.3s cubic-bezier(0.25, 0.46, 0.45, 0.94);
  z-index: 1000;
  overflow: hidden;
}

.animated-dropdown:hover .dropdown-content {
  opacity: 1;
  visibility: visible;
  transform: scale(1) translateY(0);
}

.animated-dropdown .dropdown-item {
  display: block;
  padding: 12px 16px;
  text-decoration: none;
  color: #333;
  position: relative;
  overflow: hidden;
  transition: all 0.3s ease;
}

.animated-dropdown .dropdown-item::before {
  content: "";
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255,255,255,0.4), transparent);
  transition: left 0.5s ease;
}

.animated-dropdown .dropdown-item:hover::before {
  left: 100%;
}

.animated-dropdown .dropdown-item:hover {
  background-color: #007bff;
  color: white;
  transform: translateX(5px);
}

/* Responsive Dropdown */
@media screen and (max-width: 768px) {
  .dropdown-content {
    position: static;
    opacity: 1;
    visibility: visible;
    transform: none;
    box-shadow: none;
    border-radius: 0;
    width: 100%;
  }

  .mega-content {
    position: static;
    opacity: 1;
    visibility: visible;
    transform: none;
    padding: 15px;
  }

  .mega-columns {
    grid-template-columns: 1fr;
    gap: 15px;
  }
}

