/* Standard box model example */
.standard-box {
  width: 300px;
  height: 200px;
  padding: 20px;
  border: 2px solid black;
  margin: 10px;
  background-color: lightblue;
}
/* Total width = 300px + 40px + 4px + 20px = 364px */
/* Total height = 200px + 40px + 4px + 20px = 264px */

/* Border-box model example */
.border-box {
  box-sizing: border-box;
  width: 300px;
  height: 200px;
  padding: 20px;
  border: 2px solid black;
  margin: 10px;
  background-color: lightgreen;
}
/* Total width = 300px + 20px = 320px */
/* Content width = 300px - 40px - 4px = 256px */

/* Universal border-box (recommended) */
* {
  box-sizing: border-box;
}

/* Box model visualization */
.box-demo {
  width: 200px;
  height: 100px;
  padding: 15px;
  border: 3px solid #333;
  margin: 20px;
  background-color: #f0f0f0;
  background-clip: content-box; /* Shows content area */
}

/* Responsive box with border-box */
.responsive-box {
  box-sizing: border-box;
  width: 100%;
  max-width: 500px;
  padding: 20px;
  border: 1px solid #ddd;
  margin: 0 auto;
}

