examples:
/* First line and first letter */
p::first-line {
  font-weight: bold;
  color: #333;
  text-transform: uppercase;
}

p::first-letter {
  font-size: 3em;
  float: left;
  line-height: 1;
  margin-right: 8px;
  margin-top: 2px;
  color: #007bff;
}

/* Selection styling */
::selection {
  background-color: #007bff;
  color: white;
}

::-moz-selection {
  background-color: #007bff;
  color: white;
}

/* Before and after decorations */
.quote::before {
  content: """;
  font-size: 2em;
  color: #ccc;
  position: absolute;
  left: -20px;
  top: -10px;
}

.quote::after {
  content: """;
  font-size: 2em;
  color: #ccc;
  position: absolute;
  right: -20px;
  bottom: -20px;
}

/* Icon before text */
.icon-text::before {
  content: "📧";
  margin-right: 8px;
}

/* Tooltip using pseudo-elements */
.tooltip {
  position: relative;
  cursor: help;
}

.tooltip::after {
  content: attr(data-tooltip);
  position: absolute;
  bottom: 100%;
  left: 50%;
  transform: translateX(-50%);
  background: #333;
  color: white;
  padding: 8px 12px;
  border-radius: 4px;
  font-size: 14px;
  white-space: nowrap;
  opacity: 0;
  visibility: hidden;
  transition: all 0.3s ease;
}

.tooltip:hover::after {
  opacity: 1;
  visibility: visible;
}

/* Clearfix using pseudo-element */
.clearfix::after {
  content: "";
  display: table;
  clear: both;
}

/* Overlay effect */
.image-overlay {
  position: relative;
  overflow: hidden;
}

.image-overlay::after {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);
  opacity: 0;
  transition: opacity 0.3s ease;
}

.image-overlay:hover::after {
  opacity: 1;
}

/* Counter using pseudo-elements */
.counter {
  counter-reset: item;
}

.counter li {
  counter-increment: item;
}

.counter li::before {
  content: counter(item) ". ";
  font-weight: bold;
  color: #007bff;
}

/* Ribbon effect */
.ribbon {
  position: relative;
  background: #007bff;
  color: white;
  padding: 10px 20px;
  margin: 20px 0;
}

.ribbon::before {
  content: "";
  position: absolute;
  left: -10px;
  top: 0;
  border: 20px solid transparent;
  border-right-color: #007bff;
}

/* Progress bar */
.progress {
  width: 100%;
  height: 20px;
  background: #f0f0f0;
  border-radius: 10px;
  overflow: hidden;
}

.progress::after {
  content: "";
  display: block;
  height: 100%;
  background: linear-gradient(90deg, #007bff, #0056b3);
  width: var(--progress, 0%);
  transition: width 0.3s ease;
}

/* Speech bubble */
.speech-bubble {
  position: relative;
  background: #f8f9fa;
  border: 1px solid #dee2e6;
  border-radius: 8px;
  padding: 15px;
  margin-bottom: 20px;
}

.speech-bubble::after {
  content: "";
  position: absolute;
  bottom: -10px;
  left: 30px;
  border: 10px solid transparent;
  border-top-color: #f8f9fa;
}

