/* Basic opacity levels */
.opacity-full {
  opacity: 1.0; /* Fully opaque */
}

.opacity-high {
  opacity: 0.8; /* 80% opaque */
}

.opacity-medium {
  opacity: 0.5; /* 50% opaque */
}

.opacity-low {
  opacity: 0.2; /* 20% opaque */
}

.opacity-invisible {
  opacity: 0; /* Fully transparent */
}

/* Hover effects with opacity */
.fade-on-hover {
  opacity: 1;
  transition: opacity 0.3s ease;
}

.fade-on-hover:hover {
  opacity: 0.7;
}

.reveal-on-hover {
  opacity: 0;
  transition: opacity 0.3s ease;
}

.reveal-on-hover:hover {
  opacity: 1;
}

/* Image gallery with opacity */
.gallery-item {
  opacity: 0.6;
  transition: all 0.3s ease;
  cursor: pointer;
}

.gallery-item:hover {
  opacity: 1;
  transform: scale(1.05);
}

/* RGBA vs Opacity comparison */
.rgba-background {
  background-color: rgba(0, 123, 255, 0.5);
  color: #333; /* Text remains fully opaque */
}

.opacity-element {
  background-color: #007bff;
  opacity: 0.5; /* Affects entire element including text */
}

/* Overlay effects */
.overlay-container {
  position: relative;
  overflow: hidden;
}

.overlay {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(0, 0, 0, 0.7);
  opacity: 0;
  transition: opacity 0.3s ease;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
}

.overlay-container:hover .overlay {
  opacity: 1;
}

/* Loading state */
.loading {
  opacity: 0.5;
  pointer-events: none;
  position: relative;
}

.loading::after {
  content: "Loading...";
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background: rgba(255, 255, 255, 0.9);
  padding: 10px 20px;
  border-radius: 4px;
}

/* Disabled state */
.disabled {
  opacity: 0.4;
  cursor: not-allowed;
  pointer-events: none;
}

/* Modal backdrop */
.modal-backdrop {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(0, 0, 0, 0.5);
  opacity: 0;
  visibility: hidden;
  transition: all 0.3s ease;
}

.modal-backdrop.show {
  opacity: 1;
  visibility: visible;
}

/* Fade in animation */
@keyframes fadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

.fade-in {
  animation: fadeIn 0.5s ease-in;
}

/* Pulse effect */
@keyframes pulse {
  0%, 100% {
    opacity: 1;
  }
  50% {
    opacity: 0.5;
  }
}

.pulse {
  animation: pulse 2s infinite;
}

/* Cross-browser opacity (legacy) */
.cross-browser-opacity {
  opacity: 0.5;
  filter: alpha(opacity=50); /* IE8 and earlier */
  -moz-opacity: 0.5; /* Firefox < 0.9 */
  -khtml-opacity: 0.5; /* Safari 1.x */
}

/* Tooltip with opacity */
.tooltip-opacity {
  position: relative;
}

.tooltip-opacity .tooltip-text {
  position: absolute;
  bottom: 100%;
  left: 50%;
  transform: translateX(-50%);
  background: #333;
  color: white;
  padding: 8px 12px;
  border-radius: 4px;
  opacity: 0;
  visibility: hidden;
  transition: all 0.3s ease;
}

.tooltip-opacity:hover .tooltip-text {
  opacity: 1;
  visibility: visible;
}

