}

// JavaScript Keywords (reserved words)
// break, case, catch, class, const, continue, debugger, default, delete
// do, else, export, extends, false, finally, for, function, if, import
// in, instanceof, let, new, null, return, super, switch, this, throw
// true, try, typeof, var, void, while, with, yield

// Examples of keyword usage
var myVariable = 5;           // var keyword
function myFunction() {}      // function keyword
if (true) {}                  // if keyword
for (var i = 0; i < 5; i++) {} // for keyword
return "Hello";               // return keyword

// Comments
// This is a single-line comment
var x = 5; // Comment at end of line

/*
This is a multi-line comment
It can span multiple lines
Useful for longer explanations
*/

var y = 10; /* Inline comment */ var z = 15;

// Case Sensitivity Examples
var firstName = "John";       // Camel case
var FirstName = "Jane";       // Pascal case
var firstname = "Bob";        // Lowercase
var FIRSTNAME = "Alice";      // Uppercase

// These are all different variables!
console.log(firstName);       // "John"
console.log(FirstName);       // "Jane"
console.log(firstname);       // "Bob"
console.log(FIRSTNAME);       // "Alice"

// Function names are also case sensitive
function myFunction() {
  return "Hello";
}

function MyFunction() {
  return "World";
}

console.log(myFunction());    // "Hello"
console.log(MyFunction());    // "World"

// JavaScript Identifiers
