var _validName2 = "Valid";    // Starts with underscore
var $validName3 = "Valid";    // Starts with dollar sign
var valid123 = "Valid";       // Contains numbers

// Invalid identifiers (will cause errors)
// var 123invalid = "Invalid"; // Cannot start with number
// var my-variable = "Invalid"; // Cannot contain hyphens
// var my variable = "Invalid"; // Cannot contain spaces
// var var = "Invalid"; // Cannot use keywords

// JavaScript Literals
// Number literals
var integer = 42;
var decimal = 3.14159;
var scientific = 1.23e5;      // 123000
var negative = -25;

// String literals
var singleQuotes = 'Hello World';
var doubleQuotes = "Hello World";
