var empty = [];

// Array constructor
var colors = new Array("red", "green", "blue");
var sizes = new Array(10); // Creates array with 10 empty slots
var moreNumbers = new Array(1, 2, 3, 4);

// Accessing Array Elements
console.log(fruits[0]); // "apple" (first element)
console.log(fruits[1]); // "banana" (second element)
console.log(fruits[3]); // "grape" (fourth element)
console.log(fruits[10]); // undefined (doesn't exist)

// Modifying Array Elements
fruits[1] = "mango"; // Change "banana" to "mango"
fruits[4] = "kiwi"; // Add new element
console.log(fruits); // ["apple", "mango", "orange", "grape", "kiwi"]

// Array Length Property
console.log(fruits.length); // 5
console.log(empty.length); // 0

// Changing array length
fruits.length = 3; // Truncates array
console.log(fruits); // ["apple", "mango", "orange"]

fruits.length = 5; // Extends array with undefined elements
console.log(fruits); // ["apple", "mango", "orange", undefined, undefined]

