var animals = ["cat", "dog", "bird", "fish", "rabbit"];

// Remove elements
var removed = animals.splice(1, 2); // Remove 2 elements starting at index 1
console.log(removed); // ["dog", "bird"]
console.log(animals); // ["cat", "fish", "rabbit"]

// Add elements
animals.splice(1, 0, "hamster", "turtle"); // Add at index 1, remove 0
console.log(animals); // ["cat", "hamster", "turtle", "fish", "rabbit"]

// Replace elements
animals.splice(2, 1, "snake"); // Replace 1 element at index 2
console.log(animals); // ["cat", "hamster", "snake", "fish", "rabbit"]

// Array Methods
var numbers = [1, 2, 3, 4, 5];

// slice() - returns portion of array (doesn't modify original)
var portion = numbers.slice(1, 4); // From index 1 to 3 (4 not included)
console.log(portion); // [2, 3, 4]
console.log(numbers); // [1, 2, 3, 4, 5] (unchanged)

var lastTwo = numbers.slice(-2); // Last 2 elements
console.log(lastTwo); // [4, 5]

// concat() - joins arrays
var arr1 = [1, 2, 3];
var arr2 = [4, 5, 6];
var arr3 = [7, 8, 9];

var combined = arr1.concat(arr2);
console.log(combined); // [1, 2, 3, 4, 5, 6]

var allCombined = arr1.concat(arr2, arr3);
console.log(allCombined); // [1, 2, 3, 4, 5, 6, 7, 8, 9]

// join() - converts array to string
var words = ["Hello", "World", "JavaScript"];
var sentence = words.join(" "); // Join with space
console.log(sentence); // "Hello World JavaScript"

var csv = numbers.join(","); // Join with comma
console.log(csv); // "1,2,3,4,5"

var dashed = words.join("-");
console.log(dashed); // "Hello-World-JavaScript"

// reverse() - reverses array order (modifies original)
var letters = ["a", "b", "c", "d"];
letters.reverse();
console.log(letters); // ["d", "c", "b", "a"]

// sort() - sorts array elements (modifies original)
var unsorted = ["banana", "apple", "cherry", "date"];
unsorted.sort();
console.log(unsorted); // ["apple", "banana", "cherry", "date"]

// Sorting numbers (requires compare function)
var nums = [10, 5, 40, 25, 1000, 1];
nums.sort(); // Sorts as strings!
console.log(nums); // [1, 10, 1000, 25, 40, 5] (wrong!)

// Correct number sorting
nums.sort(function(a, b) {
  return a - b; // Ascending order
});
console.log(nums); // [1, 5, 10, 25, 40, 1000]

// Descending order
nums.sort(function(a, b) {
  return b - a;
});
console.log(nums); // [1000, 40, 25, 10, 5, 1]

// Array Iteration
var colors = ["red", "green", "blue", "yellow"];

// Traditional for loop
for (var i = 0; i < colors.length; i++) {
  console.log(i + ": " + colors[i]);
}

// for...of loop (ES6)
for (var color of colors) {
  console.log(color);
}

// for...in loop (gets indices)
for (var index in colors) {
  console.log(index + ": " + colors[index]);
}

// forEach() method
colors.forEach(function(color, index) {
  console.log(index + ": " + color);
});

// Arrow function with forEach
colors.forEach((color, index) => {
  console.log(`${index}: ${color}`);
});

// map() - creates new array with transformed elements
var numbers = [1, 2, 3, 4, 5];
var doubled = numbers.map(function(num) {
  return num * 2;
});
console.log(doubled); // [2, 4, 6, 8, 10]
console.log(numbers); // [1, 2, 3, 4, 5] (unchanged)

// map() with arrow function
var squared = numbers.map(num => num * num);
console.log(squared); // [1, 4, 9, 16, 25]

// filter() - creates new array with filtered elements
var ages = [12, 18, 25, 30, 16, 21];
var adults = ages.filter(function(age) {
  return age >= 18;
});
console.log(adults); // [18, 25, 30, 21]

// filter() with arrow function
var evenNumbers = numbers.filter(num => num % 2 === 0);
console.log(evenNumbers); // [2, 4]

// reduce() - reduces array to single value
var sum = numbers.reduce(function(total, num) {
  return total + num;
}, 0); // 0 is initial value
console.log(sum); // 15

// reduce() with arrow function
var product = numbers.reduce((total, num) => total * num, 1);
console.log(product); // 120

// Find maximum value with reduce
var max = numbers.reduce((max, num) => num > max ? num : max);
console.log(max); // 5

// find() - returns first element that matches condition
var people = [
  {name: "John", age: 25},
  {name: "Jane", age: 30},
  {name: "Bob", age: 35}
];

var person = people.find(function(p) {
  return p.age > 28;
});
console.log(person); // {name: "Jane", age: 30}

// findIndex() - returns index of first matching element
var index = people.findIndex(p => p.name === "Bob");
console.log(index); // 2

// some() - tests if at least one element passes test
var hasAdult = ages.some(age => age >= 18);
console.log(hasAdult); // true

// every() - tests if all elements pass test
var allAdults = ages.every(age => age >= 18);
console.log(allAdults); // false

// includes() - checks if array contains element
console.log(colors.includes("red")); // true
console.log(colors.includes("purple")); // false

// indexOf() - returns first index of element
console.log(colors.indexOf("blue")); // 2
console.log(colors.indexOf("purple")); // -1 (not found)

// lastIndexOf() - returns last index of element
var repeated = ["a", "b", "c", "b", "d"];
console.log(repeated.lastIndexOf("b")); // 3

// Multidimensional Arrays
var matrix = [
  [1, 2, 3],
  [4, 5, 6],
  [7, 8, 9]
];

console.log(matrix[1][2]); // 6 (row 1, column 2)

// Iterating through 2D array
for (var i = 0; i < matrix.length; i++) {
  for (var j = 0; j < matrix[i].length; j++) {
    console.log(`matrix[${i}][${j}] = ${matrix[i][j]}`);
  }
}

// Array of objects
var students = [
  {name: "Alice", grade: 85, subject: "Math"},
  {name: "Bob", grade: 92, subject: "Science"},
  {name: "Charlie", grade: 78, subject: "English"}
];

// Filter students with grade > 80
var topStudents = students.filter(student => student.grade > 80);
console.log(topStudents);

// Get all student names
var names = students.map(student => student.name);
console.log(names); // ["Alice", "Bob", "Charlie"]

// Calculate average grade
var averageGrade = students.reduce((sum, student) => sum + student.grade, 0) / students.length;
console.log(averageGrade); // 85

// Array Destructuring (ES6)
var fruits = ["apple", "banana", "orange"];
var [first, second, third] = fruits;
console.log(first); // "apple"
console.log(second); // "banana"
console.log(third); // "orange"

// Skip elements
var [firstFruit, , thirdFruit] = fruits;
console.log(firstFruit); // "apple"
console.log(thirdFruit); // "orange"

// Rest operator
var [head, ...tail] = fruits;
console.log(head); // "apple"
console.log(tail); // ["banana", "orange"]

// Spread Operator (ES6)
var arr1 = [1, 2, 3];
var arr2 = [4, 5, 6];
var combined = [...arr1, ...arr2];
console.log(combined); // [1, 2, 3, 4, 5, 6]

// Copy array
var original = [1, 2, 3];
var copy = [...original];
console.log(copy); // [1, 2, 3]

// Array.from() - creates array from array-like object
var str = "hello";
var charArray = Array.from(str);
console.log(charArray); // ["h", "e", "l", "l", "o"]

// Array.isArray() - checks if value is array
console.log(Array.isArray(fruits)); // true
console.log(Array.isArray("hello")); // false
console.log(Array.isArray({})); // false

