// Creating Objects
// Object literal (most common)
var person = {
  firstName: "John",
  lastName: "Doe",
  age: 30,
  city: "New York",
  isEmployed: true
};

// Empty object
var emptyObj = {};

// Object constructor
var car = new Object();
car.make = "Toyota";
car.model = "Camry";
car.year = 2022;

// Object with methods
var calculator = {
  x: 0,
  y: 0,
  setValues: function(a, b) {
    this.x = a;
    this.y = b;
  },
  add: function() {
    return this.x + this.y;
  },
  multiply: function() {
    return this.x * this.y;
  }
};

// Accessing Object Properties
// Dot notation
console.log(person.firstName); // "John"
console.log(person.age); // 30

// Bracket notation
console.log(person["lastName"]); // "Doe"
console.log(person["city"]); // "New York"

// Dynamic property access
var propertyName = "age";
console.log(person[propertyName]); // 30

// Accessing non-existent property
console.log(person.salary); // undefined

// Modifying Object Properties
person.age = 31; // Change existing property
person.country = "USA"; // Add new property
person["email"] = "john@example.com"; // Add with bracket notation

console.log(person);
// {
//   firstName: "John",
