//   city: "New York",
//   isEmployed: true,
//   country: "USA",
//   email: "john@example.com"
// }

// Deleting Properties
delete person.isEmployed;
console.log(person.isEmployed); // undefined

// Object Methods
var student = {
  name: "Alice",
  grades: [85, 92, 78, 96],

  // Method to calculate average
  getAverage: function() {
    var sum = this.grades.reduce(function(total, grade) {
      return total + grade;
    }, 0);
    return sum / this.grades.length;
  },

  // Method to add grade
  addGrade: function(grade) {
    this.grades.push(grade);
  },

  // Method to get full info
  getInfo: function() {
    return this.name + " has an average of " + this.getAverage().toFixed(2);
  }
};

console.log(student.getAverage()); // 87.75
student.addGrade(88);
console.log(student.getInfo()); // "Alice has an average of 87.80"

// Constructor Functions
function Person(firstName, lastName, age) {
  this.firstName = firstName;
  this.lastName = lastName;
